<?phpinclude_once('config.php');session_start();M_Users::Instance()->ClearSessions();$info = explode('/', $_GET['q']);		$params = array();foreach ($info as $v){	if ($v != '')		$params[] = $v;}//print_r($params);$action = 'action_';$action .= (isset($params[1])) ? $params[1] : 'index';switch ($params[0]){	case 'page':		$controller = new C_Page();		break;	case 'auth':				$controller = new C_Auth();		break;    case '404':        $controller = new C_NoPage();        break;	case 'articles':		$controller = new C_Articles();		break;	default:		$controller = new C_Articles();}$controller->Request($action, $params);